import { useEffect, useState } from 'react';
import { getTasks, updateTask, deleteTask } from '../services/api';

const Home = () => {
  const [tasks, setTasks] = useState([]);
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [newTitle, setNewTitle] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error('Erro ao buscar tarefas:', error);
    }
  };

  const handleUpdate = async (taskId) => {
    if (!newTitle.trim()) return; // Evita título vazio
    try {
      const taskToUpdate = tasks.find((task) => task.id === taskId);
      
      if (!taskToUpdate) return;
  
      const updatedTask = {
        ...taskToUpdate,
        title: newTitle, // Mantém os outros campos e só altera o título
      };
  
      await updateTask(taskId, updatedTask);
      setEditingTaskId(null);
      setNewTitle('');
      fetchTasks();
    } catch (error) {
      console.error('Erro ao atualizar tarefa:', error);
    }
  };
  

  const handleDelete = async (taskId) => {
    try {
      await deleteTask(taskId);
      fetchTasks();
    } catch (error) {
      console.error('Erro ao deletar tarefa:', error);
    }
  };

  return (
    <div>
      <h2>Minhas Tarefas</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {editingTaskId === task.id ? (
              <>
                <input 
                  type="text" 
                  value={newTitle} 
                  onChange={(e) => setNewTitle(e.target.value)} 
                />
                <button onClick={() => handleUpdate(task.id)}>Salvar</button>
                <button onClick={() => setEditingTaskId(null)}>Cancelar</button>
              </>
            ) : (
              <>
                {task.title} - {task.date} {task.time}
                <button onClick={() => { setEditingTaskId(task.id); setNewTitle(task.title); }}>Editar</button>
                <button onClick={() => handleDelete(task.id)}>Deletar</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
