import { useEffect, useState } from 'react';
import { getTasks, updateTask, deleteTask } from '../services/api';

const Home = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const data = await getTasks();
      setTasks(data);
    };
    fetchTasks();
  }, []);

  const handleUpdate = async (taskId, newTitle) => {
    try {
      await updateTask(taskId, { title: newTitle });
      setTasks(tasks.map(task => 
        task.id === taskId ? { ...task, title: newTitle } : task
      ));
    } catch (error) {
      console.error('Erro ao atualizar tarefa', error);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('Erro ao deletar tarefa', error);
    }
  };

  return (
    <div>
      <h2>Minhas Tarefas</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.title} - {task.date} {task.time}
            <button onClick={() => {
              const newTitle = prompt('Novo título:', task.title);
              if (newTitle) handleUpdate(task.id, newTitle);
            }}>
              Editar
            </button>
            <button onClick={() => handleDelete(task.id)}>Deletar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
