export const TaskItem = ({ task, onUpdate, onDelete }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [newTitle, setNewTitle] = useState(task.title);
    const [newDescription, setNewDescription] = useState(task.description);
    const [newDate, setNewDate] = useState(task.date);
    const [newTime, setNewTime] = useState(task.time);
  
    const handleUpdate = () => {
      onUpdate(task.id, {
        title: newTitle,
        description: newDescription,
        date: newDate,
        time: newTime,
      });
      setIsEditing(false);
    };
  
    return (
      <div>
        {isEditing ? (
          <div>
            <input 
              type="text" 
              value={newTitle} 
              onChange={(e) => setNewTitle(e.target.value)}
              placeholder="Título"
            />
            <input 
              type="text" 
              value={newDescription} 
              onChange={(e) => setNewDescription(e.target.value)}
              placeholder="Descrição"
            />
            <input 
              type="date" 
              value={newDate} 
              onChange={(e) => setNewDate(e.target.value)}
            />
            <input 
              type="time" 
              value={newTime} 
              onChange={(e) => setNewTime(e.target.value)}
            />
            <button onClick={handleUpdate}>Salvar</button>
          </div>
        ) : (
          <div>
            <p>{task.title} - {task.description} - {task.date} {task.time}</p>
            <button onClick={() => setIsEditing(true)}>Editar</button>
            <button onClick={() => onDelete(task.id)}>Deletar</button>
          </div>
        )}
      </div>
    );
  };
